client/src/pages/admin/attendance.tsx.content:
import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { useAuth } from '@/lib/auth';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Users, Clock, UserCheck, UserX, Calendar, Download } from 'lucide-react';
import { format, parseISO } from 'date-fns';
import * as XLSX from 'xlsx';
import { useToast } from '@/hooks/use-toast';
import { BarChart, Bar, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

interface Attendance {
  id: string;
  userId: string;
  userName?: string;
  date: string;
  checkIn: string;
  checkOut: string | null;
  totalHours: string | null;
  status: string;
}

export default function AdminAttendancePage() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().slice(0, 10));

  // Default to last 30 days for export
  const thirtyDaysAgo = new Date();
  thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
  const [exportStartDate, setExportStartDate] = useState(thirtyDaysAgo.toISOString().slice(0, 10));
  const [exportEndDate, setExportEndDate] = useState(new Date().toISOString().slice(0, 10));

  // Get company attendance with date range based on export filters
  const { data: attendanceRecords, isLoading } = useQuery<Attendance[]>({
    queryKey: ['/api/attendance/company', { startDate: exportStartDate, endDate: exportEndDate }],
    enabled: !!user,
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  // Filter by selected date - handle different date formats
  const filteredRecords = attendanceRecords?.filter(a => {
    const recordDate = new Date(a.date).toISOString().slice(0, 10);
    return recordDate === selectedDate;
  }) || [];

  // Get today's records - handle different date formats
  const todayRecords = attendanceRecords?.filter(a => {
    const recordDate = new Date(a.date).toISOString().slice(0, 10);
    const todayDate = new Date().toISOString().slice(0, 10);
    return recordDate === todayDate;
  }) || [];

  // Count present employees (those with checkIn and status present)
  const presentCount = todayRecords.filter(a => 
    a.status === 'present' && a.checkIn
  ).length;

  // Count active employees (present and not checked out)
  const activeCount = todayRecords.filter(a => 
    a.status === 'present' && a.checkIn && !a.checkOut
  ).length;

  // Export to Excel function
  const handleExportToExcel = () => {
    if (!attendanceRecords || attendanceRecords.length === 0) {
      toast({
        title: "No data to export",
        description: "There are no attendance records available.",
        variant: "destructive",
      });
      return;
    }

    // Filter records by export date range
    const exportRecords = attendanceRecords.filter(record => {
      const recordDate = new Date(record.date).toISOString().slice(0, 10);
      return recordDate >= exportStartDate && recordDate <= exportEndDate;
    });

    if (exportRecords.length === 0) {
      toast({
        title: "No records found",
        description: `No attendance records found between ${format(new Date(exportStartDate), 'MMM dd, yyyy')} and ${format(new Date(exportEndDate), 'MMM dd, yyyy')}.`,
        variant: "destructive",
      });
      return;
    }

    // Prepare data for Excel
    const excelData = exportRecords.map(record => ({
      'Employee': record.userName || 'N/A',
      'Date': format(parseISO(record.date), 'MMM dd, yyyy'),
      'Check-in': format(parseISO(record.checkIn), 'hh:mm a'),
      'Check-out': record.checkOut ? format(parseISO(record.checkOut), 'hh:mm a') : 'Not checked out',
      'Total Hours': record.totalHours || '-',
      'Status': record.status,
    }));

    // Create workbook and worksheet
    const ws = XLSX.utils.json_to_sheet(excelData);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Attendance Report');

    // Set column widths
    const colWidths = [
      { wch: 25 }, // Employee
      { wch: 15 }, // Date
      { wch: 12 }, // Check-in
      { wch: 12 }, // Check-out
      { wch: 12 }, // Total Hours
      { wch: 10 }, // Status
    ];
    ws['!cols'] = colWidths;

    // Generate filename with date range
    const filename = `Attendance_${exportStartDate}_to_${exportEndDate}.xlsx`;

    // Save file
    XLSX.writeFile(wb, filename);

    toast({
      title: "Export successful",
      description: `Exported ${exportRecords.length} attendance records to ${filename}`,
    });
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-serif font-bold" data-testid="text-admin-attendance-title">
          Attendance Management
        </h1>
        <p className="text-muted-foreground mt-1">Monitor employee attendance and work hours</p>
      </div>

      {/* Stats */}
      <div className="grid gap-6 md:grid-cols-4">
        <Card className="hover-elevate">
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Present Today</CardTitle>
            <UserCheck className="h-4 w-4 text-ring" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-present-today">
              {presentCount || 0}
            </div>
            <p className="text-xs text-muted-foreground mt-1">
              {todayRecords.length > 0 ? `${todayRecords.length} total records today` : 'No records today'}
            </p>
          </CardContent>
        </Card>
        <Card className="hover-elevate">
          <CardHeader className="flex flex-row items-center justify-between gap-2 space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Currently Active</CardTitle>
            <Clock className="h-4 w-4 text-ring" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-active-now">
              {activeCount || 0}
            </div>
            <p className="text-xs text-muted-foreground mt-1">
              {activeCount > 0 ? 'Currently working' : 'No active employees'}
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Attendance Visualization */}
      <div className="grid gap-6 lg:grid-cols-2">
        {/* Daily Attendance Pie Chart */}
        <Card>
          <CardHeader>
            <CardTitle>Attendance Overview</CardTitle>
            <CardDescription>
              Attendance breakdown for {format(parseISO(selectedDate), 'MMMM dd, yyyy')}
            </CardDescription>
          </CardHeader>
          <CardContent>
            {filteredRecords.length > 0 ? (
              (() => {
                const presentCount = filteredRecords.filter(r => r.status === 'present').length;
                const absentCount = filteredRecords.filter(r => r.status === 'absent').length;
                const leaveCount = filteredRecords.filter(r => r.status === 'leave').length;

                const chartData = [
                  { name: 'Present', value: presentCount, color: 'hsl(142, 76%, 36%)' },
                  { name: 'Absent', value: absentCount, color: 'hsl(0, 84%, 60%)' },
                  { name: 'On Leave', value: leaveCount, color: 'hsl(24, 95%, 53%)' },
                ].filter(item => item.value > 0);

                return chartData.length > 0 ? (
                  <div className="space-y-4">
                    <ResponsiveContainer width="100%" height={250}>
                      <PieChart>
                        <Pie
                          data={chartData}
                          cx="50%"
                          cy="50%"
                          labelLine={false}
                          label={({ name, value }) => `${name}: ${value}`}
                          innerRadius={60}
                          outerRadius={100}
                          fill="hsl(var(--chart-1))"
                          dataKey="value"
                        >
                          {chartData.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={entry.color} />
                          ))}
                        </Pie>
                        <Tooltip />
                      </PieChart>
                    </ResponsiveContainer>

                    <div className="grid grid-cols-3 gap-4 text-center">
                      {chartData.map((item) => (
                        <div key={item.name} className="space-y-1">
                          <div className="text-2xl font-bold" style={{ color: item.color }}>
                            {item.value}
                          </div>
                          <div className="text-xs text-muted-foreground">{item.name}</div>
                        </div>
                      ))}
                    </div>
                  </div>
                ) : (
                  <div className="flex items-center justify-center h-[300px] text-muted-foreground">
                    No attendance data for selected date
                  </div>
                );
              })()
            ) : (
              <div className="flex items-center justify-center h-[300px] text-muted-foreground">
                No attendance data for selected date
              </div>
            )}
          </CardContent>
        </Card>

        {/* Attendance Status Bar Chart */}
        <Card>
          <CardHeader>
            <CardTitle>Daily Attendance Stats</CardTitle>
            <CardDescription>
              Visual comparison of attendance status
            </CardDescription>
          </CardHeader>
          <CardContent>
            {filteredRecords.length > 0 ? (
              (() => {
                const presentCount = filteredRecords.filter(r => r.status === 'present').length;
                const absentCount = filteredRecords.filter(r => r.status === 'absent').length;
                const leaveCount = filteredRecords.filter(r => r.status === 'leave').length;

                const barData = [
                  {
                    category: 'Status',
                    Present: presentCount,
                    Absent: absentCount,
                    'On Leave': leaveCount,
                  },
                ];

                return (
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={barData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="category" />
                      <YAxis />
                      <Tooltip />
                      <Legend />
                      <Bar dataKey="Present" fill="hsl(142, 76%, 36%)" />
                      <Bar dataKey="Absent" fill="hsl(0, 84%, 60%)" />
                      <Bar dataKey="On Leave" fill="hsl(24, 95%, 53%)" />
                    </BarChart>
                  </ResponsiveContainer>
                );
              })()
            ) : (
              <div className="flex items-center justify-center h-[300px] text-muted-foreground">
                No attendance data for selected date
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Export to Excel Section */}
      <Card>
        <CardHeader>
          <div className="flex items-center gap-2">
            <Download className="h-5 w-5 text-ring" />
            <CardTitle>Export Attendance</CardTitle>
          </div>
          <CardDescription>Download attendance records in Excel format for selected date range</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid gap-4 md:grid-cols-3">
            <div className="space-y-2">
              <Label htmlFor="export-start-date">Start Date</Label>
              <Input
                id="export-start-date"
                type="date"
                value={exportStartDate}
                onChange={(e) => setExportStartDate(e.target.value)}
                data-testid="input-export-start-date"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="export-end-date">End Date</Label>
              <Input
                id="export-end-date"
                type="date"
                value={exportEndDate}
                onChange={(e) => setExportEndDate(e.target.value)}
                data-testid="input-export-end-date"
              />
            </div>
            <div className="flex items-end">
              <Button
                onClick={handleExportToExcel}
                className="w-full"
                data-testid="button-export-excel"
              >
                <Download className="h-4 w-4 mr-2" />
                Export to Excel
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Attendance Records */}
      <Card>
        <CardHeader>
          <div className="flex flex-wrap items-center justify-between gap-4">
            <div>
              <CardTitle>Attendance Records</CardTitle>
              <CardDescription>View employee attendance by date</CardDescription>
            </div>
            <div className="flex items-center gap-2">
              <Calendar className="h-4 w-4 text-muted-foreground" />
              <input
                type="date"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                className="px-3 py-1.5 border rounded-md text-sm"
                data-testid="input-date-filter"
              />
            </div>
          </div>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="text-center py-8 text-muted-foreground">Loading attendance...</div>
          ) : filteredRecords.length > 0 ? (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Employee</TableHead>
                  <TableHead>Date</TableHead>
                  <TableHead>Check-in</TableHead>
                  <TableHead>Check-out</TableHead>
                  <TableHead>Total Hours</TableHead>
                  <TableHead>Status</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredRecords.map((record) => (
                  <TableRow key={record.id} data-testid={`attendance-row-${record.id}`}>
                    <TableCell className="font-medium">{record.userName || 'Employee'}</TableCell>
                    <TableCell>{format(parseISO(record.date), 'MMM dd, yyyy')}</TableCell>
                    <TableCell>{format(parseISO(record.checkIn), 'hh:mm a')}</TableCell>
                    <TableCell>
                      {record.checkOut ? format(parseISO(record.checkOut), 'hh:mm a') : '-'}
                    </TableCell>
                    <TableCell>{record.totalHours || '-'}</TableCell>
                    <TableCell>
                      <Badge variant={record.status === 'present' ? 'default' : 'secondary'}>
                        {record.status}
                      </Badge>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              No attendance records for {format(parseISO(selectedDate), 'MMMM dd, yyyy')}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

