            code: 'MEALS',
            maxLimit: 2000.00,
            requiresReceipt: true,
            isActive: true,
          },
          {
            companyId: company.id,
            name: 'Communication',
            code: 'COMM',
            maxLimit: 1000.00,
            requiresReceipt: true,
            isActive: true,
          },
          {
            companyId: company.id,
            name: 'Other Expenses',
            code: 'OTHER',
            maxLimit: 3000.00,
